//ROUTING UTIL
var UTIL = {
  fire : function(func, funcname, args){
    var namespace = Routes;  // indicate your obj literal namespace here
    funcname = (funcname === undefined) ? 'init' : funcname;
    if (func !== '' && namespace[func] && typeof namespace[func][funcname] == 'function'){
      namespace[func][funcname](args);
    }

  },
  loadEvents : function(eventName){
    var bodyId = document.body.id;
	eventName = (eventName === undefined) ? '' : eventName;
	if(eventName=='') {
		// hit up common first.
		UTIL.fire('common');
		// do all the classes too.
		//jQuery.each(document.body.className.split(/\s+/),function(i,classnm){
		jQuery.each(document.getElementById('container').className.split(/\s+/),function(i,classnm){
		  UTIL.fire(classnm);
		  UTIL.fire(classnm,bodyId);
		});
		UTIL.fire('common','finalize');
	} else {
		UTIL.fire('common',eventName);
		jQuery.each(document.getElementById('container').className.split(/\s+/),function(i,classnm){
		  UTIL.fire(classnm,eventName);
		});
	}
  }
};

