/* DEVICE ORIENTATION */
function deviceOrientationHandler(e){
	$('html').removeClass('portrait landscape');
	switch (window.orientation) {
		case 0:
		case 180:
			$('html').addClass('portrait');
			if (window.deviceOrientation !== 'portrait') {
				$('input').blur();

				if ($('.fullpage').length) {
					$('.fullpage').trigger('click');
					$(window).smartresize();

					// bug fix: where there's a blank/empty space after device rotate, specifically fullpage on ipad.
					$(window).scrollTop(0);
				};
			}
			window.deviceOrientation = 'portrait';
			break
		case 90:
		case -90:
			$('html').addClass('landscape');
			if (window.deviceOrientation !== 'landscape') {
				$('input').blur();

				if ($('.fullpage').length) {
					$('.fullpage').trigger('click');
					$(window).smartresize();

					// bug fix: where there's a blank/empty space after device rotate, specifically fullpage on ipad.
					$(window).scrollTop(0);
				};
			}
			window.deviceOrientation = 'landscape';
			break
	}
}
if (window.DeviceOrientationEvent) {
	window.addEventListener('deviceorientation', deviceOrientationHandler, false);
} else {
	// console.error('Device orientation not supported in this browser.');
}

// Read a page's GET URL variables and return them as an associative array.
function getUrlVars()
{
    var vars = [], hash;
    var hashes = window.location.href.slice(window.location.href.indexOf('?') + 1).split('&');
    for(var i = 0; i < hashes.length; i++)
    {
        hash = hashes[i].split('=');
        vars.push(hash[0]);
        vars[hash[0]] = hash[1];
    }
    return vars;
}

