                // get the text areas
                $( formSelector + ' textarea' ).each( function(){

                    var nameAttr = $( this ).data( 'name' );

                    if( convertCases ) {
                       nameAttr = _this.hyphenToCamelCase( nameAttr );
                    }

                    object[ nameAttr ] = $( this ).val();

                });


                // Drop empty attributes
                if( dropEmpty ) {

                    $.each( object, function( key, value ) {

                        if( object[ key ] == '' ) {

                            delete object[ key ];
                        }
                    });
