							{ el: '#sb-smt-code-freebie-editor_ifr', height: 175 }
						,	{ el: '#sb-smt-tool-review-editor_tbl', height: 249 }
					];

				$.each( editorsTbl, function( k, el ) {
					// $( el ).css( 'width', 511 );
					$( el ).css( 'width', '100%' );
				});

				$.each( editorIfr, function( k, el ) {
					$( el.el ).css( 'height', el.height );
				});

